let TileMapInstance, TileObjectReferencesInstance;

const TileMap = function() {
    if(TileMapInstance)
        return TileMapInstance;
    TileMapInstance = new Set();
    return TileMapInstance;
};

const TileObjectReferences = function() {
    if(TileObjectReferencesInstance)
        return TileObjectReferencesInstance;
    TileObjectReferencesInstance = new Set();
    return TileObjectReferencesInstance;
};

gdjs.__AutoTiling = {
    TileMap,
    TileObjectReferences
};

const TileMap = new gdjs.__AutoTiling.TileMap();

eventsFunctionContext.returnValue = TileMap.has([
    'x',eventsFunctionContext.getArgument('X'),
    'y',eventsFunctionContext.getArgument('Y')
].join(''));

const objects = Array.from(new gdjs.__AutoTiling.TileObjectReferences());

if(objects.size == 0) return;

const SceneVariables = runtimeScene.getVariables().get('__AutoTiling').getAllChildren();
const TileMap = new gdjs.__AutoTiling.TileMap();

function checkMap(x,y) {
    return TileMap.has([
        'x',x,
        'y',y
    ].join(''))*1;

}

// Only scan changedTiles and its adjacent tiles
const TileScan = {
    X: SceneVariables.X.getAsNumber(),
    Y: SceneVariables.Y.getAsNumber()
};

const dimension = objects[0].getWidth();

let toScanCoordinates = {};

toScanCoordinates[[
    'x',TileScan.X,
    'y',TileScan.Y
].join('')] = true;

toScanCoordinates[[
    'x',TileScan.X+dimension,
    'y',TileScan.Y
].join('')] = true;

toScanCoordinates[[
    'x',TileScan.X-dimension,
    'y',TileScan.Y
].join('')] = true;

toScanCoordinates[[
    'x',TileScan.X,
    'y',TileScan.Y+dimension
].join('')] = true;

toScanCoordinates[[
    'x',TileScan.X,
    'y',TileScan.Y-dimension
].join('')] = true;

objects.forEach(SpriteObject => {

    // filter only adjacent tiles
    if(toScanCoordinates[[
        'x',SpriteObject.getX(),
        'y',SpriteObject.getY()
    ].join('')] == undefined) return;

    let frame = 0;

    // Check Down
    frame += checkMap(SpriteObject.getX(),SpriteObject.getY()+dimension)*8;

    // Check left
    frame += checkMap(SpriteObject.getX()-dimension,SpriteObject.getY())*4;

    // Check Up
    frame += checkMap(SpriteObject.getX(),SpriteObject.getY()-dimension)*2;

    // Check Right
    frame += checkMap(SpriteObject.getX()+dimension,SpriteObject.getY())*1;

    // Change tile to the appropriate map
    SpriteObject.setAnimationFrame(frame);
});
