/**
 * Masking in GDevelop
 * @author arthuro555
 */


// First we need to get the objects
let mask = eventsFunctionContext.getObjects("Mask")[0];
let masked = eventsFunctionContext.getObjects("Masked")[0];

// We get the renderer of the masked object...
let maskedRenderer = masked.getRendererObject(); 
// ...and apply the masks renderer to his mask property
maskedRenderer.mask = mask.getRendererObject();

/**
 * Mask removal in GDevelop
 * @author arthuro555
 */


// First we need to get the masked object (the one the mask has been applied to)
let masked = eventsFunctionContext.getObjects("Masked")[0];

// We get the renderer...
let maskedRenderer = masked.getRendererObject(); 
// And set the mask to nothing.
maskedRenderer.mask = null;

/**
 * Mask check in GDevelop
 * @author arthuro555
 */


// First we need to get the potentially masked object
let masked = runtimeScene.getObjects("Masked")[0];

// We get the renderer...
let maskedRenderer = masked.getRendererObject(); 
// ...and return the condition result
eventsFunctionContext.returnValue = maskedRenderer.mask !== null;

const phone = eventsFunctionContext.getArgument("phone");
const n = /[0-9 -()+]+$/;

eventsFunctionContext.returnValue = n.test(phone);

const num = eventsFunctionContext.getArgument("num");
const n = /^-?\d+\.?\d*$/;

eventsFunctionContext.returnValue = n.test(num);

const lrt = eventsFunctionContext.getArgument("lrt");
const n = /^[a-zA-Z\s]*$/;

eventsFunctionContext.returnValue = n.test(lrt);
