function fibonacci(n) {
    let initial = 0;
    let final = 1;
    let sum = 0;

    let i = 0;
    for(i = 2; i <= n; i++) {
        sum = initial + final;

        initial = final;
        final = sum;
    }
    return n ? final : initial;
}

