window.scene = runtimeScene;
variables = scene.getVariables();

//currenttime of video object, for more see DEBUG group
currentTime = Math.round(variables.get("exp_currentTime").getAsNumber());
currentTime_formated = variables.get("currentTime_formated");

//duration of video object, for more see debug group
duration = Math.round(variables.get("exp_duration").getAsNumber());
duration_formated = variables.get("duration_formated"); //this init the variable



function format_time(time_second) {
    if (Number.isInteger(time_second)) {
        date = new Date(null);
        date.setSeconds(time_second);
        if (time_second >= 3600) {
            return date.toISOString().substr(11, 8); // MM:SS
        } else {
            return date.toISOString().substr(14, 5); // HH:MM:SS
        }


    }
}

//update the new variables with time formated, ready to use in this group Time display
duration_formated.setString(format_time(duration));
currentTime_formated.setString(format_time(currentTime));
